mapper.select:
select ftaskid,fwelderid,fmachineid,fname,fwelder_no welderno,weldername,fequipment_no,fwelded_junction_no fjunction_no, frealstarttime,frealendtime, fchannel,felectricity electricity,fvoltage voltage,sum(alarmtime)+sum(warntime) warntime,sum(worktime)-sum(alarmtime) worktime from ( select t.ftaskid,t.fwelderid,t.fmachineid, i.fname,w.fwelder_no,w.fname weldername,m.fequipment_no, j.fwelded_junction_no,t.frealstarttime, t.frealendtime,fchannel,avg(felectricity) felectricity, AVG(fvoltage) fvoltage,0 alarmtime,0 warntime ,sum(tw.fworktime) worktime from tb_taskresult t left JOIN tb_welder w on w.fid = t.fwelderid left join tb_welding_machine m on m.fid = t.fmachineid left JOIN tb_work tw on tw.fwelder_id = t.fwelderid and tw.fmachine_id = t.fmachineid and tw.fjunction_id = t.ftaskid left JOIN tb_welded_junction j on j.fid= t.ftaskid LEFT JOIN tb_insframework i on i.fid = tw.fjunction_itemid left JOIN tb_insframework ins on ins.fid = i.fparent left JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
and w.fwelder_no like ${welderno}
and fwelded_junction_no like ${taskno}
and frealstarttime >= #{dtoTime1}
and (frealendtime <= #{dtoTime2} or frealendtime is null)
group by t.ftaskid,t.fwelderid,t.fmachineid,i.fname,w.fwelder_no,w.fname,m.fequipment_no,j.fwelded_junction_no,t.frealstarttime, t.frealendtime,fchannel
UNION ALL select t.ftaskid,t.fwelderid,t.fmachineid, i.fname,w.fwelder_no,w.fname weldername,m.fequipment_no, j.fwelded_junction_no,t.frealstarttime, t.frealendtime,fchannel,avg(felectricity) felectricity, AVG(fvoltage) fvoltage,0 alarmtime,sum(tw.fwarntime) warntime ,0 worktime from tb_taskresult t left JOIN tb_welder w on w.fid = t.fwelderid left join tb_welding_machine m on m.fid = t.fmachineid left JOIN tb_warn tw on tw.fwelder_id = t.fwelderid and tw.fmachine_id = t.fmachineid and tw.fjunction_id = t.ftaskid left JOIN tb_welded_junction j on j.fid= t.ftaskid LEFT JOIN tb_insframework i on i.fid = tw.fjunction_itemid left JOIN tb_insframework ins on ins.fid = i.fparent left JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
and w.fwelder_no like ${welderno}
and fwelded_junction_no like ${taskno}
and frealstarttime >= #{dtoTime1}
and (frealendtime <= #{dtoTime2} or frealendtime is null)
group by t.ftaskid,t.fwelderid,t.fmachineid,i.fname,w.fwelder_no,w.fname,m.fequipment_no,j.fwelded_junction_no,t.frealstarttime, t.frealendtime,fchannel ) temp group by ftaskid,fwelderid,fmachineid,fname,fwelder_no, weldername,fequipment_no, fwelded_junction_no,frealstarttime,frealendtime,fchannel,felectricity,fvoltage

select ftaskid,fwelderid,fmachineid,frealstarttime,frealendtime,foperatetype type,i.fid, i.fname,w.fwelder_no welderno,w.fname weldername,j.fwelded_junction_no fjunction_no,m.fequipment_no from tb_taskresult t LEFT JOIN tb_welding_machine m on m.fid = t.fmachineid LEFT JOIN tb_welder w on w.fid = t.fwelderid LEFT JOIN tb_welded_junction j on j.fid = t.ftaskid LEFT JOIN tb_insframework i on i.fid = j.fitemId left JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
and w.fwelder_no like ${welderno}
and j.fwelded_junction_no like ${taskno}
and frealstarttime >= #{dtoTime1}
and (frealendtime <= #{dtoTime2} or frealendtime is null)
group by ftaskid,fwelderid,fmachineid,frealstarttime,frealendtime,foperatetype ,i.fid, i.fname,w.fwelder_no,w.fname ,j.fwelded_junction_no,m.fequipment_no order by ftaskid,frealstarttime,frealendtime

select sum(num) from(
select sum(fworktime) num from tb_work w
where 1=1


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


UNION ALL
select sum(fstandbytime) num from tb_standby w
where 1=1


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


UNION ALL
select sum(fwarntime) num from tb_warn w
where w.fstatus !=99


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


)temp

SELECT sum(electricity) electricity,sum(voltage) voltage,sum(worktime) worktime,sum(time) time from (
select avg(felectricity) electricity,avg(fvoltage) voltage,sum(fworktime) worktime,0 time from tb_work w
where 1=1


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


union all
select 0 electricity,0 voltage,0 worktime,sum(falarmtime) time from tb_alarm w
where 1=1


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')


)temp

select sum(fstandbytime) worktime from tb_standby w
where 1=1


        and fjunction_id = #{dto.junctionid}


        and w.FPRODUCT_NUMBER_ID = #{dto.bloc}


        and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')


        and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')

SELECT * from ( select avg(felectricity) electricity,avg(fvoltage) voltage,sum(fworktime) worktime,sum((w.fworktime*w.fwirefeedrate*m.fcoefficient)/60) wirefeedrate from tb_work w INNER JOIN tb_insframework i on i.fid = w.FWELDER_ITEMID left JOIN tb_insframework ins on ins.fid = i.fparent left JOIN tb_insframework insf on insf.fid = ins.fparent LEFT JOIN tb_material_coefficient m on w.fmaterialgas = m.fmaterial and w.fwirediameter = m.fdiameter and w.fmachinemodel=m.fmodel where 1=1
and (i.fid=#{itemid} or ins.fid=#{itemid} or insf.fid=#{itemid} or insf.fparent=#{itemid})
and fmachine_id = #{dto.machineid}
and fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')
and fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')
)temp

SELECT w.fid fid,w.fwelder_no serialnumber,w.fname,i.fname insname FROM tb_welder w inner join tb_insframework i on w.Fowner=i.fid left JOIN tb_insframework ins on ins.fid = i.fparent left JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
and w.fname like #{dto.junctionno}
and w.fwelder_no like #{dto.welderno}








SELECT fid fid,junction_name fvaluename FROM tb_junction
WHERE 1=1

    and ${junctionno}

select junction_name insname,sum(insid) insid,sum(hour) worktime from( select j.fid,j.JUNCTION_NAME, 0 insid, 0 hour from tb_junction j WHERE 1=1
and j.JUNCTION_NAME like #{junctionno}
union all SELECT j.fid, j.JUNCTION_NAME,SUM(a.fworktime) insid, 0 hour FROM tb_junction j INNER JOIN tb_work a ON j.fid=a.fjunction_id WHERE 1=1
and a.fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')
and a.fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')
and a.FPRODUCT_NUMBER_ID = #{dto.bloc}
and j.JUNCTION_NAME like #{junctionno}
GROUP BY j.fid, j.JUNCTION_NAME UNION ALL SELECT j.fid, j.JUNCTION_NAME, 0 insid,SUM(a.fworktime) hour FROM tb_junction j INNER JOIN tb_work a ON j.fid=a.fjunction_id LEFT JOIN tb_production_craft p on j.fid = p.fjunction LEFT JOIN tb_library_junction l on p.fid = l.production_id LEFT JOIN tb_welded_junction e on l.trackingcard_id = e.fid WHERE a.fstatus=99
and a.fstarttime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')
and a.fendtime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')
and a.FPRODUCT_NUMBER_ID = #{dto.bloc}
and j.JUNCTION_NAME like #{junctionno}
GROUP BY j.fid, j.JUNCTION_NAME )temp group by fid,JUNCTION_NAME,insid order by fid

SELECT m.fequipment_no fname,i.fname insname,d.fvaluename,FWeldTime time FROM tb_live_data l INNER JOIN tb_welding_machine m ON m.fid = l.fmachine_id INNER JOIN tb_insframework i on i.fid = l.fmachine_itemid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent INNER join tb_dictionary d on d.fvalue=l.fstatus where d.ftypeid=15
and fmachine_id=#{id}
and d.fvalue=#{value}
and FWeldTime >= to_date(#{dto.dtoTime1}, 'yyyy-mm-dd hh24:mi:ss')
and FWeldTime <= to_date(#{dto.dtoTime2}, 'yyyy-mm-dd hh24:mi:ss')
and (i.fid=#{dto.parent} or ins.fid=#{dto.parent} or insf.fid=#{dto.parent} or insf.fparent=#{dto.parent})

select MIN(k.FREALSTARTTIME) startTime,MAX(k.FREALENDTIME) endTime from TB_TASKRESULT k
where FOPERATETYPE=2

    and k.fwelderid = #{dto.welderno}


    and k.fcard_id = #{dto.junctionid}


    and k.FCRAFT_ID = #{dto.personid}


    and${tasktime}

select * from ( SELECT wt.weldTime time,i.fid insid,i.fname insname,m.fid,m.fequipment_no machineno,fwelded_junction_no taskno, t.weldtime starttime,CASE WHEN fwelded_junction_no IS null THEN 1 ELSE 2 END AS type FROM ( ${sql} ) wt left JOIN tb_welding_machine m on 1=1 left JOIN tb_insframework i on i.fid = m.finsframework_id LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent left JOIN ( SELECT m.fid,m.fequipment_no,j.fwelded_junction_no,DATE_FORMAT( t.frealstarttime, '%Y-%m-%d') weldtime FROM tb_welding_machine m INNER JOIN tb_taskresult t on t.fmachineid = m.fid INNER JOIN tb_welded_junction j on j.fid = t.ftaskid group by m.fid,weldtime) t on t.fid = m.fid and t.weldtime = wt.weldtime where 1=1
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
)temp where 1=1
and type = #{type}
order by fid,time,type

SELECT ${filed} name,l.FWeldTime time FROM tb_live_data l WHERE (fstatus='3' or fstatus='5' or fstatus='7' or fstatus='99')
${str}
ORDER BY l.FWELDTIME
