mapper.select:
SELECT w.fid fid,max(w.fIP) fIP,max(w.fmodel) model,max(fstatus_id) fstatus_id,max(dict.fvaluename) modelname, max(i.fid) iid,max(ftype_id) ftype_id,wm_concat(g.fid) fmacurl,max(dic.fvalue) mid,fequipment_no,g.fgather_no, max(fjoin_time) fjoin_time,max(i.fname) iname,max(dic.fvaluename) mname,max(fisnetworking) fisnetworking, max(fposition) fposition,max(d.fvaluename) statusname,max(di.fvaluename) typename FROM tb_welding_machine w LEFT JOIN tb_insframework i ON w.finsframework_id = i.fid LEFT JOIN tb_dictionary d ON d.fvalue = w.fstatus_id AND d.ftypeid=3 LEFT JOIN tb_dictionary di ON di.fvalue = w.ftype_id AND di.ftypeid=4 LEFT JOIN tb_dictionary dic ON dic.fvalue = w.fmanufacturer_id AND dic.ftypeid=14 LEFT JOIN tb_dictionary dict ON dict.fvalue = w.fmodel AND dict.ftypeid=17 LEFT JOIN tb_gather g ON w.FGATHER_ID = g.fid where 1 = 1
and ${parent}
and ${str}
GROUP BY w.FID,fequipment_no,g.fgather_no ORDER BY nlssort(w.fequipment_no,'NLS_SORT=SCHINESE_PINYIN_M')

SELECT fid
FROM tb_welding_machine
where fequipment_no = #{eno}

SELECT fid
FROM tb_welding_machine
where finsframework_id = #{insfId}

SELECT fid, fequipment_no
FROM tb_welding_machine
where finsframework_id = #{id}

SELECT count(*)
FROM tb_welding_machine
WHERE fequipment_no = #{eno}

SELECT count(*) FROM tb_welding_machine m
inner join tb_gather g on m.FGATHER_ID = g.FID
where 1 = 1

    AND m.finsframework_id=#{itemid}


    AND g.fgather_no=#{gather}

SELECT fid
FROM tb_welding_machine
WHERE fgather_id = #{gatherid}

mapper.update:
UPDATE tb_welding_machine
SET fgather_id=NULL
where fid = #{wid}

UPDATE tb_welding_machine
SET fstatus_id=#{status}
where fid = #{wid}

UPDATE tb_gather
SET FMACHINE_ID=NULL
WHERE FMACHINE_ID = #{machineId}

UPDATE tb_gather
SET FMACHINE_ID=#{machineId}
WHERE fid = #{gatherId}

mapper.select:
SELECT COUNT(1)
FROM tb_welding_machine
WHERE FGATHER_ID = #{gather_id}

select.if:
SELECT * FROM (
SELECT wm.FID,wm.FIP,wm.FEQUIPMENT_NO,g.FGATHER_NO
FROM TB_WELDING_MACHINE wm LEFT JOIN TB_GATHER g ON wm.FGATHER_ID = g.FID
WHERE wm.FIP = #{ip}
) WHERE 1 >= ROWNUM

mapper.select:
SELECT * FROM ( SELECT wm.FID,wm.FIP,wm.FTYPE_ID,FEQUIPMENT_NO,FSTATUS_ID,FINSFRAMEWORK_ID,wm.FGATHER_ID,wm.FMODEL model FROM TB_WELDING_MACHINE wm LEFT JOIN TB_GATHER g ON wm.FGATHER_ID = g.FID WHERE 1 = 1
AND g.FGATHER_NO = #{gather_no}
) WHERE 1 >= ROWNUM
