mapper.select:
select tb_users.id id,tb_users.users_name userName,users_password userPassword,tb_users.users_login_name userLoginName, tb_users.users_phone userPhone,tb_users.users_email userEmail,tb_users.users_insframework userInsframework, tb_users.users_position userPosition,tb_users.status status,tb_insframework.fid insid,tb_insframework.fname insname,d.fvaluename statusname, tb_users.ISSUEWPS,tb_users.RECEIVEALARM from tb_users INNER JOIN tb_dictionary d on d.fvalue = tb_users.status left join tb_insframework on tb_users.users_insframework=tb_insframework.fid where d.ftypeid=6
and tb_insframework.fid = #{parent}
and ${str}
ORDER by nlssort(tb_users.id,'NLS_SORT=SCHINESE_PINYIN_M')
