mapper.select:
select sum(time) from ( select sum(fworktime) time from tb_work l INNER JOIN tb_insframework i on i.fid = l.fmachine_itemid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent where fstarttime >= #{time} and fendtime < date_sub(date_sub(#{time},interval -1 day),interval 0 minute)
AND (i.fid=#{dto.parent} or ins.fid = #{dto.parent} or insf.fid = #{dto.parent} or insf.fparent=#{dto.parent})
and fmachine_id = #{mid}
UNION ALL select sum(fstandbytime) time from tb_standby l INNER JOIN tb_insframework i on i.fid = l.fmachine_itemid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent where fstarttime >= #{time} and fendtime < date_sub(date_sub(#{time},interval -1 day),interval 0 minute)
AND (i.fid=#{dto.parent} or ins.fid = #{dto.parent} or insf.fid = #{dto.parent} or insf.fparent=#{dto.parent})
and fmachine_id = #{mid}
)temp

SELECT sum(fworktime)/60/60 time, DATE_FORMAT( fstarttime, '%m' ) month FROM tb_work w INNER JOIN tb_insframework i on i.fid = w.fmachine_itemid LEFT join tb_insframework ins on ins.fid = i.fparent LEFT join tb_insframework insf on insf.fid = ins.fparent WHERE fstarttime like ${year}
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
group by month

SELECT count(j.fid) total,date_format(fcreatedate,'%m') month FROM tb_welded_junction j INNER JOIN tb_insframework i on i.fid = j.fitemId LEFT join tb_insframework ins on ins.fid = i.fparent LEFT join tb_insframework insf on insf.fid = ins.fparent WHERE fcreatedate like ${year}
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
group by month

select count(num) total,month from ( SELECT DISTINCT(ftaskid) num,date_format(frealendtime,'%m') month FROM tb_taskresult t INNER JOIN tb_welded_junction j on t.ftaskid = j.fid INNER JOIN tb_insframework i on i.fid = j.fitemId LEFT join tb_insframework ins on ins.fid = i.fparent LEFT join tb_insframework insf on insf.fid = ins.fparent WHERE frealendtime like ${year}
and (i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
group by ftaskid,fmachineid,fwelderid,month )temp
